css:
@-moz-document domain("uspdigital.usp.br") {
body,html{
 	width: 100%;  
}
#layout_cabecalho{
 	width: 100% !important;
  	
}
#layout_cabecalho a{
 	font-size: 2rem !important; 
}

#layout_principal{
	width: 100% !important; 
}

#layout_conteudo{
  	width: 100% !important;
  	background: #ddd;  
  	padding: 0 !important;
  	float: left;
  	font-size: 3rem;
}
#layout_conteudo a{
 	font-size: 40px !important; 
}
#layout_conteudo table{
 	margin: 0;
  	width: 100%;
}
#layout_conteudo td{
 	height: 65px;
  	font-size: 3.5rem !important;
}
#layout_conteudo td[role="gridcell"]{
 	height: 65px;
  	font-size: 1.7rem !important;
}
#layout_conteudo font{
	font-size: 3.5rem;
}
#layout_conteudo input,
#layout_conteudo select{
 	width: 100%;
  	height: 75px;
  	font-size: 3rem;
}

#layout_menu{
  	padding: 0 !important;
  	width: 100% !important;
}
.menulist{
   	width: 100% !important;   
}
.menulist li,
.menulist li a{
   	font-size: 3.5rem !important; 
 	display: block;
  	height: 80px;
}
}
