css:
@-moz-document domain("tweetdeck.twitter.com") {
.avatar {
  border-radius: 50%;
  box-shadow: 0 0 12px turquoise;
  transition: all 0.4s ease-out;
}

.avatar:hover {
  box-shadow: 0 0 24px turquoise;
  transition: all 0.4s ease-out;
}

.is-favorite .tweet-header .account-link .tweet-img .avatar, .is-favorite .account-summary .item-img .avatar {
  transform: rotate(360deg) scale(1) skew(1deg) translate(0px);
  -webkit-transform: rotate(360deg) scale(1) skew(1deg) translate(0px);
  -moz-transform: rotate(360deg) scale(1) skew(1deg) translate(0px);
  -o-transform: rotate(360deg) scale(1) skew(1deg) translate(0px);
  -ms-transform: rotate(360deg) scale(1) skew(1deg) translate(0px);
  box-shadow: 0 0 12px #FF8B00;
}

.is-favorite .tweet-header .account-link .tweet-img .avatar:hover, .is-favorite .account-summary .item-img .avatar:hover {
  box-shadow: 0 0 24px #FF8B00;
}
            
.is-retweet .tweet-header .account-link .tweet-img .avatar, .is-retweet .account-summary .item-img .avatar {
  transform: rotate(360deg) scale(1) skew(1deg) translate(0px);
  -webkit-transform: rotate(360deg) scale(1) skew(1deg) translate(0px);
  -moz-transform: rotate(360deg) scale(1) skew(1deg) translate(0px);
  -o-transform: rotate(360deg) scale(1) skew(1deg) translate(0px);
  -ms-transform: rotate(360deg) scale(1) skew(1deg) translate(0px);
  box-shadow: 0 0 12px #89CE5F;
}

.is-retweet .tweet-header .account-link .tweet-img .avatar:hover, .is-retweet .account-summary .item-img .avatar:hover {
  box-shadow: 0 0 24px #89CE5F;
}

.is-favorite.is-retweet .tweet-header .account-link .tweet-img .avatar, .is-favorite.is-retweet .account-summary .item-img .avatar {
  transform: rotate(720deg) scale(1) skew(1deg) translate(0px);
  -webkit-transform: rotate(720deg) scale(1) skew(1deg) translate(0px);
  -moz-transform: rotate(720deg) scale(1) skew(1deg) translate(0px);
  -o-transform: rotate(720deg) scale(1) skew(1deg) translate(0px);
  -ms-transform: rotate(720deg) scale(1) skew(1deg) translate(0px);
  box-shadow: 2px 2px 8px #FF8B00, -2px -2px 8px #89CE5F;  
}

.is-favorite.is-retweet .tweet-header .account-link .tweet-img .avatar:hover, .is-favorite.is-retweet .account-summary .item-img .avatar:hover {
  box-shadow: 2px 2px 16px #FF8B00, -2px -2px 16px #89CE5F;  
}
}
