css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wired.com") {
  
  /* No dumb underlines */
  .link-underline a {
    box-shadow: 0px 0px 0px #000000 inset;
  }
  .link-underline-sm a {
    box-shadow: none;
  }
  
  /* Make content 900+px wide */
  .content {
    left: 0px;
    margin-right: 0px;
  }
  
  /* Pull quotes further to the left so they don't flow into the article */
  /* This goes offscreen on smaller monitors; just hide it */
  .content .pullquote {
    margin: 64px 64px 64px -400px;
    display: none;
  }
  .content .carve {
    margin: 64px 64px 64px -400px;
  }
  
  /* Get rid of all the free space at the top of the page */
  body {
    padding-top: 0px;
    text-rendering: optimizeLegibility;
  }
  
  /* Uncomment this to disable the image fade-in animation */
  /* 
  .fader {
    opacity: 1;
    filter: none;
    transform: translate3d(0px, 0px, 0px);
  }
  */
  
  /* Hide the useless bits */
  #sidebar {
    display: none;
  }
  #prefooter {
    display: none;
  }
  #global-footer {
    display: none;
  }
  #article-tags {
    display: none;
  }
  .headroom {
    display: none;
  }
  #global-header {
    display: none;
  }
  #sharing {
    display: none;
  }
  .paginate {
    display: none;
  }
}
