css:
@-moz-document domain('themekraft.com'){
    
    /* Removing persistent masthead */
    #masthead .site-nav-wrap.navbar.navbar-default.navbar-fixed-top { position:static !important }
    
    /* Darken post color */
    .main-content-inner .post .header-single h1{ color:#444 !important }
    .main-content-inner .post .entry-content p { color:#333 !important }
    .main-content-inner .post .entry-content a { color:#558915 !important }
}
