css:
@-moz-document domain("leho.howest.be") {

  body, #footer-wrap{
    width: 50% !important;
    position: relative;
  }
  
body {
   transform: scale(2.0);
   transform-origin: 0% 0%;
}

#container, #menu, #usermenu, #corps, #leftmenu, #logoWrapper, #socialWrapper, #languageWrapper, #footer-wrap, #corps div{
  width: 100% !important;
}

  #logoWrapper, #corps #content .user_course_category .notifications {
    padding-top: 10px;
  }
  #languageWrapper, #logoWrapper, #loginWrapper, #footer-wrap, 
  #corps #content .user_course_category .course{
    padding-left: 10px;
  }
  
  #socialWrapper ul{
    padding-right: 10px;
  }
  
  #corps{
    padding-bottom: 0;
  }
#corps #leftmenu {
    float: left;
    width: 260px;
    margin-right: 0;
}
#announcementlist .announcement .announcement_content {
    width: 100% !important;
    padding: 8px;
    margin-bottom: 8px;
    margin-left: 0;
}
  #announcementlist .announcement .announcement_content .invisible{
    height: 60px  !important;
  }
 #announcementlist .announcement .announcement_content .invisible .right{
   float: left;
 }
#users_table_top_right{
    padding: 10px 40px 0 0;
  }
  
#users_table_top_right span{
  margin-left: 40px;
}
#corps #content{
  overflow: visible;
}

  #corps #content .user_course_category .notifications {
    float: none;
    text-align: left !important;
  }
  
  #corps #content .user_course_category .icon img{
    height: 0;
}
#footer-wrap{
  height: auto;
}
.boxedContent .content{
    padding: 0;
    padding-left: 10px;
    padding-top: 10px;
  }
  
#header #usermenu ul {
    float: right;
    margin-top: 30px;
    margin-right: 30px;
}
  
#header #menu ul li {
    padding: 0px;
    margin: 0px;
    list-style: outside none none;
    width: 25%;
    height: 80px;
    overflow: hidden;
}

#corps #leftmenu li a {
    background: none repeat scroll 0% 0% #EA148C;
    color: #FFF;
    text-decoration: none;
    font-family: "VAGRoundedStdBold";
    display: block;
    margin-right: 0;
    height: 34px;
    line-height: 34px;
    overflow: hidden;
    font-size: 1.077em;
    padding-left: 40px;
    transition: all 0.2s ease-in 0s;
}
  
#corps .eight{
    margin: 0 0 10px 0;
  }
  #tools .threecolumn {
    margin-left: 0;
    width: 100%;
}
  #tools .tool a .icon{
    position: absolute;
    left: 2px;
    transform: scaleX(-1);
  }
  
  .bodyform span select{
    width: 100%;
  }

}
