css:
@-moz-document domain("tvtime.com") {
.home,#home,#profile,.main-block,.modal-content, .settings, .comments-filters li a, .row.show-nav{
  background-color: #111 !important;
}

html, H1,H2,.page-right,.profile-nav,.total-watch-time,.facorite-genres,.badges-group,.leaderboards,.post,.sub-comment,.modal-header,.tab-content,.fanart-container, .episode-infos, .actor-header .infos, .role, #shows-results, #profile-stats, ul.dropdown-menu{
  background-color: #222 !important;
}

.leaderboard-infos,.reply .holder,.reply .holder .mentions,input,select,button,textarea,.btn-tvst,.twitter-message-info,.tab-basic a, .right, .comments-filters li a.active, .dropdown, .external-meme-tab, .dropzone, li a:hover{
  background-color: #333 !important;
}

li.row.episode-wrapper.odd , li.active a{
    background-color: #444 !important;
}

.badge-title,.tab-rewards.active .label,.tab-rewards.active .count,.stat-item,.no-input,input,select,label,button,textarea,p,.btn-tvst,.twitter-message-info, .emotions .active .emotion-label, .emotions .active .emotion-count, .external-meme-tab, .comments-filters li *{
  color: #AAA !important;
}

H1,H2, H3,a,.data-popover{
  color: #FFF !important;
}

li[role=presentation] .label{
    color: white !important;
}
}
