css:
@-moz-document domain(twog.fr) {
body{ 
  background: #111 !important; 
} 
.logo-outer-wrapper,.body-wrapper{ 
  background-color: #222 !important; 
}

.EmbeddedTweet-ancestor {
    background-color: #444;
}

.logo-left img{ 
  filter: invert(1); 
  -webkit-filter: invert(1); 
  -moz-filter: invert(1);
  -o-filter: invert(1); 
  -ms-filter: invert(1); 
}

H1,H2,H3,a{
  color: #FFF !important;
}

p{
  color: #AAA !important;
}

.EmbeddedTweet-tweet{
    background-color: #333;
}

.FollowButton.follow-button.profile{
    color: #55acee !important;
}
}
