css:
/* i really want this to be global */

body[bgcolor="white"]  {
    background-color: #222;
}
body[bgcolor="white"], body[bgcolor="white"] b {
    color: #aaa;
}
body[bgcolor="white"] h1, body[bgcolor="white"] h2, body[bgcolor="white"] font[color="red"] {
    color: #eaab00;
}

body[bgcolor="white"] table {
    background-color: #2d3868;
}

body[bgcolor="white"] .expandable {
    color: #007acc;
}

body[bgcolor="white"] .expandable {
    transition: text-shadow 0.25s;
}

body[bgcolor="white"] .expandable:hover {
    text-shadow: 0 0 2px #007acc;
}
