css:
@-moz-document url-prefix("http://bombparty.sparklinlabs.com/play/twitch/") {
#App, #GameCanvas {
	margin: 0!important;
	width: 70%!important;
	height: 90%!important;
	background: transparent!important;
	position: absolute!important;
	z-index: 10!important;
}

#JoinGameButtonContainer {
	z-index: 999!important;
}
 
#Sidebar {
	width: 100%!important;
  	height: 100%!important;
	float: left!important;
	background: #101010!important;
	position: absolute!important;
	z-index: 5!important;
}
 
#AppsBar, #Sidebar .NuclearSupportBox.Show, #ChatLogPanel {
	display: none!important;
	position: absolute!important;
	z-index: -99!important;
}

#SidebarTabButtons {
	margin-left: 70%!important;
}

#EmbeddedChat, #SettingsTab, #LeaderboardTab {
	margin-left: 70%!important;
	width: 30%!important;
	height: 95%!important;
	position: absolute!important;
}
 
.StreamBox, .StreamBox object {
	float: left!important;
	width: 70%!important;
	height: 100%!important;
	position: relative!important;
}

.StreamBox iframe {
	width: 100%!important;
	height: 100%!important;
}

#GameCanvas, div.StreamBox object {
	width: 100%!important;
	height: 100%!important;
}
}
