css:
@-moz-document domain("reddit.com") {   

/*Removes login side banner*/
body>div.side {
display:none;}

/*Removes user information*/
span.user{
  display:none !important}

/*Remove my Subreddits bar*/
span.selected.title{
  display:none !important}

/*Remove comment capability*/
.comments{
    display:none !important}
  
/*Remove Share Button*/
.share{
      display:none !important}
    
/*Removes author tag*/
.author{
        display:none !important}

}
