css:
/* i really want this to be global */
.moz-text-flowed, .moz-text-plain, .moz-text-html {
  font: 14px/1.3 Inconsolata, "Droid Sans" !important;
}

@-moz-document url(chrome://conversations/content/stub.xhtml)
{

/* changes background color of the message body*/
body {
  background-color: #eee !important;
  font: 14px/1.5 Inconsolata, "Droid Sans" !important;
}

#conversationHeader, .attachmentThumb, button {
  background-image: none !important;
  background-color: rgba(235, 235, 235, 0.9) !important;
}

/*removes background image of the buttons in the header and changes the background color*/
button {
  background-color: inherit !important;
  border: 0;
  box-shadow: none !important;
}

.messageHeader.hbox {
  display: block;
}
.star, .author, .involved.boxFlex {
  float: left;
}

.options     { clear: left}
.detailsLine { font-size: inherit !important }
.smallEmail  { display: none !important }

/*changes border around  the message and eliminates the shadow*/
.message {
  border: 1px solid #ccc !important;
  border-radius: 0 !important;
  box-shadow: 0px 5px 5px rgb(200, 200, 200) !important;
  margin: 10px 10px !important;
}
.message.collapsed {
  height: 3rem !important;
  margin: 0px 10px !important;
}
.message.highlight, .message:focus {
  border: 2px solid rgb(102, 183, 237) !important;
  box-shadow: 0px 5px 5px rgba(102, 183, 237, 0.6) !important;
}

.attachHeader    { display: block !important }
.attachmentThumb { border: 0 !important }

.messageFooter {
  background-color: inherit !important;
  padding: 2px 10px !important;
  border: 1px solid #ddd !important;
}
.messageFooter button {
  border: 0 !important;
  color: #999 !important;
}
.messageFooter button:hover {
  color: #333 !important;
}
.messageFooter button .ext {
  background-image: none !important;
}





}
