css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hyperphysics.phy-astr.gsu.edu") {

  body {
    background-color: #333 !important;
    color: #ccc !important;
  }
  
  a {
    color: #D9C057 !important;
  }
  
  a:active, a:hover {
    color: #F7DA4A !important;
  }
  
  a:visited {
    color: #B29932 !important;
  }
  
  a:visited:hover {
    color: #D9C057 !important;
  }
  
  img {
    background-color: #CCC !important;
    border-radius: 4px !important;
  }
  
  img:hover {
    background-color: #FFE4C8 !important;
  }
  
  /*
  img:active {
    width: 100% !important;
    height: 100% !important;
  }
  */
  
}
