@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ligues-face-au-mal.obsidianportal.com") {
  .campaign-public-layout #content
  {
    max-width: none;
  }
  
  .collapsable-nav-row .front-nav-container 
  {
    width: 200px;
  }
  .large-3
  {
   width: calc(100% - 58.3333% - 200px);
  }
  
  .dovah
  {
    font-family: DOV3 TH9M;
  }
  
  .large-block-grid-2 > li
  {
    width: 33%;
  }
  .character-list-item-container {  
    clear: none !important;
  }
  .character-list-item-container:nth-child(3n+1) {  
    clear: both !important;
  }
}
