css:
@namespace url(http://www.w3.org/1999/xhtml);
 
@-moz-document domain("kaskus.co.id") {
 

      
  /* general */
  .sidebar, .related-thread, .quick-navigation, .tools-panel.share, #feedback, .ads-lb-wrap {
    display: none !important;
  }
 
  /* thread view */
  .main-content { width: 100%; }
  .user-control-stick { width: 900px; display: none; }
 
  /* ucp */
  .user-home-content table.post-entry tbody td:nth-child(1) { width: 5%; }
  .user-home-content table.post-entry tbody td:nth-child(2) { width: 65%; }
  .user-home-content table.post-entry tbody td:nth-child(3) { width: 10%; }
  .user-home-content table.post-entry tbody td:nth-child(4) { width: 10%; }
  .user-home-content table.post-entry tbody .author { float: left; width: auto; }
  .user-home-content table.post-entry tbody .act { float: right; margin-top: -16px; }
 
  .user-home-content table:not(.post-entry) tbody td:nth-child(1) { width: 3.3%; }
  .user-home-content table:not(.post-entry) tbody td:nth-child(2) { width: 45%; }
  .user-home-content table:not(.post-entry) tbody td:nth-child(3) { width: 6.7%; }
  .user-home-content table:not(.post-entry) tbody td:nth-child(4) { width: 35%; }
 
  /* custom arie */
  body{
   background:#ccc;
   background-size: cover;
   margin:0;
   color: black;     
   overflow-x:hidden;
  font-family: 'Ubuntu', sans-serif;
  }
  .skin{
   display:none;
  }
 
      .postlist .entry {
           font-family: 'Ubuntu', sans-serif;
           font-size: 16px;
      }
      
      .entry-body{
            background: #f1f1f1;
      }
  /* fix background */
  #refine-search > .categories-wrap {background: white;}
  .fjb.landing {background: white !important;}
  .forum.landing {background: white !important;}
 
  /* untuk disamping avatar */
  .user-info { font-size: 0px; }
  .user-info > .title, .user-info > .meta { font-size: 12px; float: left; }
  .user-info > .meta { clear: both; }
  .postlist > .author > .user-details {margin-top:0px !important;}
  /* fix width */
  .container > .row > .main-content {width: 900px !important;float:none!important;  margin-left: auto ;  margin-right: auto ;}
}
