css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sata.chatovod.ru") {
body {
  background-color: #101010;
  color: #D8D8D8;
}
.chatMain, .chatEventDiv {
  background: inherit;
}
.chatHeader {
  background: #202020;
  color: #4040FF
}
.chatFooter {
  background: #202020;
  color: #808080;
}
.chatHeader .chatTitle {
  text-shadow: 0px 0px 0px #4040FF;
}
.chatMain .chatPeople {
  background: #202020;
}
.chatMain .chatMessages {
  top: 0px
}
.chatTabs {
  
}
.chatEventDiv {
  
}
#scrollBottomBox {
  display: none;
}
a {
  color: #D8D8FF;
}
.chatFooter .chatFooterWrapper .chatPanel div a span {
  border-bottom: none;
}
.chatTabs ul {
  position: fixed;
  top: 12px;
  right: 45%;
  z-index: 100;
  left: 45%;
}
#aevents.data a.obl {
  opacity: 0;
  visibility: hidden !important;
  display: none !important;
}
.chat.withEvents #events {
  position: fixed;
  bottom: 32px;
  left: 50%;
  z-index: 100;
}
}
