css:
@-moz-document domain("hipchat.com") {
.date-block {
  background-color: #EAEAEA;
}
.date-divider span {
  background-color: #EAEAEA;
  border-bottom: 1px solid #FFF;
  display: block;
}
#hipchat .aui-navgroup-inner, body .aui-navgroup-inner {
  background-color: #3A3A3A;
}
#hipchat .hc-tab > a, body .hc-tab > a, #hipchat .hc-roster > a, body .hc-roster > a {
  color: #C8C8C8;
}
#hipchat .hc-right-sidebar-col .hc-roster, body .hc-right-sidebar-col .hc-roster {
  background-color: #3A3A3A;
}
#hipchat .hc-right-sidebar-col .hc-roster-item a.hc-roster-link, body .hc-right-sidebar-col .hc-roster-item a.hc-roster-link {
  color: #B5B5B5;
}
.hc-msg-blue {
  background: #F2F2F2;
  border-top: solid #F9F9F9 1px;
}
.hc-msg-blue + .hc-msg-nocolor, .hc-msg-purple + .hc-msg-nocolor, .hc-msg-green + .hc-msg-nocolor, .hc-msg-red + .hc-msg-nocolor, .hc-msg-yellow + .hc-msg-nocolor, .hc-msg-gray + .hc-msg-nocolor {
  border-top: solid #E0E0E0 1px;
}
#hipchat .hc-sidebar, body .hc-sidebar {
  background-color: #3A3A3A;
  height: 100%;
}
#hipchat .hc-right-sidebar-col .hc-roster-item, body .hc-right-sidebar-col .hc-roster-item {
  color: #B5B5B5;
}

.aui-nav, .aui-nav>li {
  background-color: #3A3A3A;
}
#hipchat .hc-right-sidebar-col .hc-files, body .hc-right-sidebar-col .hc-files, #hipchat .hc-right-sidebar-col .hc-links, body .hc-right-sidebar-col .hc-links {
  background-color: #3A3A3A;
}
}
