css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("guildwars2roleplayers.com") {
  
  #page-wrap {color: #888;}
  
  select.input-text {color: #aaa;}
  .input-text input {color: #aaa;}
  .input-textarea textarea {color: #aaa;}
  
  .element_pagewidget {color: #aaa;}
  
  .element_username {color: #aabbc5;}
   .element_username:focus {color: #c8d3da;}
   .element_username:hover {color: #aabbc5;}
  .votes, .votes-label {color: #5d7874;}
  
  .m_forum .breadcrumbs .text {color: #dbdacb;}
  
  .contentbox .block-title {color: #a1969e;}
  
  .m_forum .quick-navigation-block {padding-top: 0; margin-top: 15px;}
  .quick-navigation-block {overflow: hidden; width: 420px; height: 22px; background: url(http://i58.tinypic.com/runx9c.png) 393px 0px/27px 22px no-repeat; border: 1px solid #000;}
  .quick-navigation-block .element_button {position: fixed; left: 10px; bottom: 10px;}
  .quick-navigation-block select {float: none !important; background: transparent; background: rgba(0,0,0,0.2); border: none; width: 450px; height: 22px;}

}
