css:
@-moz-document url-prefix("https://trello.com/b/") {
#board {
  display:block;
  flex:initial;
  }

.list {
  display: block;
  float: left;
  width: 12.5%;
  min-width: 175px;
  margin-top: 15px;
  margin-left: 5px;
  margin-bottom: 30px;
  background: none;
  min-height: 350px;
}

.list-header {
  color: #e2e4e6;
}
.open-card-composer {
  color: #e2e4e6;
}
.open-card-composer:hover {
  background: none;
  color: #eee;
}

}
