css:
@-moz-document url-prefix("https://tweetdeck.twitter.com") {
/** column width **/
.column {width:385px !important}

/** mostly hide scroll bars **/
.js-column-scroller {width:103.5% !important;}

/** Be Careful How You Tweet **/
.compose-text-title:after {display:block;content: 'Remember: You are always one tweet away from being fired.';}

}
