css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(https://ddlvalley.),url-prefix(https://www.ddlvalley.),url-prefix(http://ddlvalley.),url-prefix(http://www.ddlvalley.) {
  
  /******** HIDE ELEMENTS ********/
  
  /*Copyright*/ #footer { display: none; }
  /*Center Text Coloring / Background*/ #middle { background:none }
  /*ShoutBox*/ .widget.cl{ display:none }
  /*Left Bar*/ .sb.fl { display:none }
  /*Bottom Bar*/ .m_bot { display:none }
  /*Posted in / Genre / Tags*/ .bot { display:none }
  /*Download Info*/ .plot { background:none !important; }
  /*Download Poster*/ .poster { background:none !important; }
  /*Home About Contact US DMCA Forum IRC*/ .menu { display: none; }
  /*Add Bar in download selection*/ .cont > center:nth-child(1)  { display: none; }
  /*Torrent Link in posts*/ .post .cont > strong a { display: none; }
  /*Episode Title and Download Site name*/.post strong  {color:darkred !important;}

  /*Global Page Size*/ .pb, #core { width: auto}
  /*Global Page Background*/ body { background: black; color:green ;margin: 50px 0px 0px; }
  /*Global Font Color*/ .post strong, .post b, .post { color:darkgray}
  /*Global Link Color Internal*/ a {color:green !important;}
  /*Change Link color External*/ a.ext-link { color: darkcyan; }
  /*Global Box Green Effect*/ /*.plot, .tracklist { box-shadow: 5px 5px 5px darkgreen;}*/
    
  /*Download Separator*/ .sep { border: 1px solid darkblue }
  /*Downloads Title*/ H2 {text-align: center}
  /*Tracklist Color*/ .tracklist { background: none !important; text-align: center }
  
  /*QuickLinks in multiple downloads list*/ 
  .Season li a { color: white;background-color: darkblue ; margin-right:5px}
  .Season { padding: 0px; text-align: center}
  .post p { margin: 0px;}

  /*Posted by ... */
  .fl { float:none }
  .meta { background:none !important; text-shadow: none;text-align: center }
  
  /*Post border mods*/
  .post hr { border: 1px solid darkblue }
  .post .cont { line-height: 150%;}
  /*In Download page separators.cont > hr {display:none}*/
  
  /*Fixed Seach Bar + DDLValley Logo*/
  #header { position: fixed ;left:0% ;top:0px ; height:initial}
  .sb.fr { position: fixed;right:0%;top:0px }
  /*DDL Valley Logo margin*/ .logo > a { margin: 0px 0px 0px; }
  /*Navigation Bar */ .wp-pagenavi  { position:absolute;left:42% }
  
  /*Comment Box*/
  .comment { background: none; border: 1px solid darkgreen; }
   textarea, .field1 input, .field2 input  { background: none !important; color:cyan ; border-color:cyan !important; }
}
