/**
 * @name JSHint wide
 * @stylish-id 112047
 * @author Niklas Gollenstede
 * @licence CC-BY-SA-4.0 or MIT
 * @description
 *     Makes the code and error sections on the JSHint main page use the whole screen (at any resolution).
 *
 *     If you have any issues with this style, please open a ticket at https://github.com/NiklasGollenstede/stylish
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://jshint.com/") {

    .sidebar {
        right: -100px;
        width: 120px;
        padding-left: 5px;
        transition: all .5s;
        z-index: 999;
    }

    .sidebar:hover {
        right: 0px;
        padding-left: 10px;
    }

    img[alt="JSHint"] {
        width: 100%;
    }

    .whiteboard {
        z-index: 999;
        right: 30px;
        left: initial;
        width: calc(40% - 10px);
    }

    .CodeMirror {
        width: calc(60% - 50px);
    }

    .CodeMirror-hscrollbar {
        min-height: 15px;
    }

}
