@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("scrumy.com") {
  
  body.default{
    background: none #555;

  }
  table#scrumy-board, table#scrumy-board th, table#scrumy-board td{
    border-color: #222;
  }
  
  #scrumy-board #todo-header, #scrumy-board #verify-header{
    background-color: #343434;
  }
 table#scrumy-board th#story-header, #scrumy-board #inprogress-header, #scrumy-board #done-header{
   background-color: #444;
 }
  
  #scrumy-board .todo, #scrumy-board .verify{
    background-color : #444;
  }
  #scrumy-board td.stories, #scrumy-board .done, #scrumy-board .inprogress,
  table#scrumy-board th#plus-header, table#scrumy-board .plus,
  #scrumy-board .new_story_row td{
    background-color : #343434;
  }
  
  .story-handle{
    background-color: #343434;
    color: #AAA;
  }
}
