css:
@-moz-document domain("toodledo.com"), domain("outline.toodledo.com"), domain("lists.toodledo.com"), domain("habits.toodledo.com") {

html, body {
color:#666 !important;
}

.tlbr_sec {
border-left: none !important;
}

#quickAdd {
  height: 16px !important;
}

.searchField {
  height: 16px !important;
}

.note {
color: #666;
margin-bottom: 3px;
font-size:12px;
line-height:17px;
}

.pri0 {
color: #42A8F3 !important;
}

.pri1 {
color: #47AF1E !important;
}

.row {
padding: 12px 0 12px 16px !important;
min-height: 0px !important;
font-size:14px !important;
}

.sec_btn:hover {
text-decoration: underline !important;
}

.pri3, .status1 {
color: #CC4C4C !important;
text-shadow: none !important;
}

.pri2, .status2 {
color: #ECC807 !important;
text-shadow: none !important;
}

.sec_btn {
color: #fff !important;
background: none !important;
}

.hov_btn {
color: #3184C5 !important;
}

.hov_btn:hover {
text-decoration: underline !important;
}

#proflinks a, #proflinks input {
color: #fff !important;
}

.tlbr_sec {
background-color: #616D7C !important;
}

.date0 {
color: #666 !important;
}

.date1 {
color: #F19A00 !important;
font-weight: bold !important;
}

.date2 {
color: #47AF1E !important;
font-weight: bold !important;
}

.date3 {
color: #CC4C4C !important;
font-weight: bold !important;
}

.cols:hover span, .cols:hover .note {
background-color: #FFF7D7 !important;
}

.row:hover {
background-color: #FFF7D7 !important;
}

.cols .note, .cols .ednote {
margin: 10px 20px 10px 20px !important;
}

#banner {
color: #fff !important;
background: #80B8FF !important;
}

#left_side {
background: #F5F5F5 !important;
}

#main {
background: #fff !important;
}

#tlbr_new {
background: #FFD245 !important;
}

.sep {
background: #D0E7FF !important;
font-weight: normal !important;
padding: 5px 4px 5px 1px !important;
text-transform:uppercase;
}

.tab:hover {
background-color: #D8DCDF !important;
}

#viewby {
padding: 20px 0px 16px 0px !important;
}

#notebookText {
padding: 20px !important;
padding-top: 30px !important;
line-height: 19px !important;
font-size:14px !important;
}

input, textarea {
border: none !important;
}


.userstylewarning {
display: none !important;
}

#left_side {
padding-top: 0px !important;
}
}
