css:
@-moz-document domain("cplusplus.com") 
{
    body 
    {
        background: #333333;
        color: #CCCCCC;
    }

    a
    {
        color: #46a2da !important;
    }

    element
    {
      height: 0px !important;
    }

    div #I_mid 
    {
        background: #222;
        color: #CCCCCC;
    }


    div #I_main 
    {
        background: #222;
        color: #CCCCCC;
    }
    
    div #I_content 
    {
        background: #222;
        color: #CCCCCC;
    }


    div #I_wrap 
    {
        background: #222;
        color: #CCCCCC;
    }

    .sect .root, ul 
    {
        background: #666666;
    }

    div.on 
    {
        background: #000000;
    }

    .C_doc .C_prototype .even 
    {
        background: #333333;
        border-color: #111111;
    }

    .C_doc #parameters dt 
    {
        background: #000000;
    }

    .source 
    {
        background: #000000;
        color: #33CC33;
    }

    .C_doc dl.links 
    {
         background: #000000;
    }

    .C_doc .links dt 
    {
         background: #000000;
    }                

    .C_doc .links dt a 
    {
         color: #0099CC;
    }                

    .C_doc .links dd 
    {
         background: #000000;
    }

    #I_nav li a 
    {
        background: #000000;
        color: #0099CC;
    }

    #I_nav li a span 
    {
        background: none;
    }

    #I_mid 
    {
        background: #000000;
    }

    pre 
    {
        background: #000000;
    }

    pre code
    {
        color: #CCC !important;
    }

    .auto .source DFN
    {
        color: #bd63c5 !important;
    }

    .auto .source CITE
    {
        color: #57A64A !important;
    }

    .snippet
    {
        color: #CCC !important;
    }
    .auto .source VAR, .namespace
    {
        color: #46a2da !important;
    }

    .auto .source KBD
    {
         color: #D69D85 !important;
    }
}
