css:
@-moz-document url-prefix("http://twinoid.com/"), url-prefix("http://www.hordes.fr/"), url-prefix("http://kingdom.muxxu.com/"), url-prefix("http://intrusion.muxxu.com/"), url-prefix("http://www.die2nite.com/"), url-prefix("http://www.dieverdammten.de/"), url-prefix("http://www.zombinoia.com/") {
/* "Disparition" de la premiére bordure et du fond des avatar */
.tid_twinoidAvatar table td {
	border : 0px !important;
	margin : 0px !important;
	padding : 0px !important;
	background-color : transparent !important;
	overflow : hidden;
	-moz-box-shadow : 0px 0px 2px transparent !important;
	-webkit-box-shadow : 0px 0px 2px transparent !important;
	box-shadow : 0px 0px 2px transparent !important;
	text-align : center;
	vertical-align : middle !important;
	outline : none !important;
}

/* "Disparition" de la seconde bordure des avatar */

.tid_userSheet td.tid_avatar .tid_twinoidAvatar {
	-moz-box-shadow : 0px 0px 2px transparent !important;
	-webkit-box-shadow : 0px 0px 2px transparent !important;
	box-shadow : 0px 0px 2px transparent !important;
}
}
