css:
@-moz-document url-prefix("http://www.learncpp.com/cpp-tutorial/") {
#header, #left, #right, colgroup, .ad-inserter, .sd-sharing-enabled, #comments, .post-footer, #respond, .commentlist, #footer, div.post table tr td img {
  display: none !important;
}
#container {
  padding: 5rem !important;
  max-width: 54rem !important;
}
table#layout {
  font-size: 1.2rem !important;
}
div.post > div > table {
  margin-top: 5rem !important;
  border-top: solid 4px #eee!important;
}
div.post > div > table tr td {
  background: transparent !important;
}
div.post > div > table tr {
  max-width: 30% !important;
}
div.post > div > table tr td:nth-child(1) {
  text-algin: center !important;
}
div.post > div > table tbody {
	margin-top: 2rem !important;
  width: 100% !important;
  display: flex !important;
  flex-direction: row-reverse !important;
  justify-content: space-between !important;
}
}
