css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("uwants.com") {

font[size="3"][color="#0000ff"] {
display: none;
}

#POP, #KFE {
display: none;
}

td[colspan="2"][align="center"][valign="top"] {
display: none;
}

td[valign="top"][align="right"]>iframe {
display: none;
}

iframe[align="center"] {
display: none;
}

#ad_text {
display: none;
}

#thread_ads_top, #thread_ads_bottom {
display: none;
}

div[align="center"],td[colspan="6"][height="35"] {
display: none;
}

[id^="loop_ads"] {
display: none;
}

td[colspan="3"] {
display: none;
}

table#rightads {
display: none;
}

iframe[name="google_ads_frame"] {
display: none;
}

span#displayiframe, span#hideiframe, #startiframe {
display: none;
}

}
