css:
@-moz-document domain("mail.yahoo.com") {
/* author: Atino Core */
/* remove elements */
  #yucs-web_search_button_wrapper, #yucs-top-bar, /* LOGO STUFF */
  #yucs-network_link, /* Yahoo HOME */
  #UHSearchWeb, #web-search-btn, #yucs-web_search_button_wrapper, /* web search button in search bar */
  #masterNav, /* top bar with quick links */
  .empty-folder-submsg2, .wrapperpane, /* ads in empty folders */
  #theAd, #slot_TL1, #slot_mbrec
  {
    display: none;
  }

  a.YLogoMY {
   float: left; 
   margin-right: 28px !important; 
   margin-left:  30px !important; 
  }

  #UH {
    width: 100% !important;
    height: 2.5em; /* top bar layer above main content */
    float: left;
  }
  #uhWrapper {
    z-index: 100;
  }

  /* smaller search and better position */
  #uhWrapper > table { 
    width: 100% !important;
    min-width: 200px !important;
    margin-right: 20px;
  }

  #uhWrapper > table tr td:first-child {
    width: 0 !important;
  }
  #mail-search-btn {
    margin-right: 20px !important; 
  }
  #UHSearchBox {
    height: 23px !important;
    margin-top: 3px !important; 
  }
  #UHSearchProperty {
    height: 23px !important;
    line-height: 23px !important;
    margin-top: 3px !important;
    padding: 0 5px !important;
  }
  #Stencil .lt .typeahead-input-container {
    min-width: 200px; 
  }
  
  /* move main under the search bar */
  #main {
    top: 2.8em !important;
  }

  /* move main content back to right border */
    #shellcontent,
    .frost-container {
    right: 0;
  }
 
  /* move main content back to right border FIREFOX */
    .wide-right-rail  #shellcontent,
    .wide-right-rail  .frost-container {
    right: 0;
  }

  /* right controls */
  #yucs .yucs-fl-right {
    margin-top: 7px !important;
  }
  
  /* less height in folder list to show more folders at once */
  .listnav .listnav-folder li {
    padding-bottom: 0px;
  }
  
  /* hide scrollbar in msg list if not needed */
  #msg-list .list-view-items {
    overflow-y: auto !important;
  }
}
