css:
@-moz-document url-prefix("http://www.escapistmagazine.com/") {
#callout_carousel_panel div {
 display: none;
}

#right_column {
    display: none !important;
}

.body,
p,
ul,
ol,
#article_content h1.headline,
#video_display h1.headline,
div.articles_post_content h1.headline,
div.news_post_content h1.headline,
#article_content div.headline,
div.articles_post_content div.headline,
div.news_post_content div.headline,
#article_content div.by,
#video_display div.by,
body#index div#site_body,
body#channel div#site_body,
#site_menu,
#menu .mainMenu {
    background-color: #101010 !important;
    color: #999 !important;
}

#site_body,
body.escbg2 #site_body,
div#club_motto_feature_panel {
    background-color: #101010 !important;
    color: #999 !important;

    background: #FFF !important; /* Old browsers */
    background: -moz-linear-gradient(top,  #101010 0%, rgba(16,16,16, 1) 50px, rgba(16,16,16, 1) 100%) !important; /* FF3.6+ */
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#101010), color-stop(2%,rgba(16,16,16, 1)), color-stop(100%,rgba(16,16,16, 0))) !important; /* Chrome,Safari4+ */
    background: -webkit-linear-gradient(top,  #101010 0%,rgba(16,16,16, 1) 50px,rgba(16,16,16, 1) 100%) !important; /* Chrome10+,Safari5.1+ */
    background: -o-linear-gradient(top,  #101010 0%,rgba(16,16,16, 1) 50px,rgba(16,16,16, 1) 100%) !important; /* Opera 11.10+ */
    background: -ms-linear-gradient(top,  #101010 0%,rgba(16,16,16, 1) 50px,rgba(16,16,16, 1) 100%) !important; /* IE10+ */
    background: linear-gradient(to bottom,  #101010 0%,rgba(16,16,16, 1) 50px,rgba(16,16,16, 1) 100%) !important; /* W3C */
}

div.grid_display_box div.box_headline a {
    text-decoration:none;
    color: #7070A0;
}

}
