/* Created by Mårten Björkman on 2015-04-08 (last updated 2015-04-08) */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('https://mittschema.mtr.se/trafik/Employee/'), url-prefix('https://mittschema.mtr.se/station/Employee/') {
  /* Only if screen size is larger than the normal page size */
  @media screen and (min-width: 820px) and (min-height: 547px) {
    html, body {
      width: 100%;
      height: 100%;
      margin: 0;
    }
    
    /* Top: Main - Home.aspx */
    #HomeForm {
      height: 100%;
    }
    #Header {
      width: 100%;
      background-color: #7d071d;
    }
    #LeftSideBar {
      min-height: calc(100% - 68px);
    }
    #EmployeeInnerView_InnerViewFrames {
      width: calc(100% - 180px - 4px);
      height: calc(100% - 68px - 4px);
    }
    #EmployeeInnerView_WholeViewFrame {
      width: 100%;
      height: 100%;
      margin: 2px;
    }

    /* Iframe: */
    /* - WorkdayCalendar.aspx */
    #WorkdayCalendarForm,
    #WorkdayCalendarForm > div:nth-of-type(2),
    /* - QuotaCalendar.aspx */
    #QuotaCalendarForm,
    #QuotaCalendarForm > div:nth-of-type(2),
    /* - OvertimeAvailabilityCalendar.aspx */
    #OvertimeAvailabilityCalendarForm,
    #OvertimeAvailabilityCalendarForm > div:nth-of-type(2),
    /* - WorkdayDetail.aspx */
    #WorkdayDetailForm,
    /* - AbsencesDetail.aspx */
    #AbsencesDetailForm,
    /* - PrintAbsences.aspx */
    #PrintAbsencesForm,
    /* - OvertimeAvailabilityPeriod.aspx */
    #OvertimeAvailabilityPeriodForm,
    /* - OvertimeAvailabilityTransactionDetail.aspx */
    #OvertimeAvailabilityTransactionDetailForm,
    /* - OvertimeAvailabilityDetail.aspx */
    #OvertimeAvailabilityDetailForm,
    /* - EmployeeProfile.aspx */
    #EmployeeProfileForm,
    /* - ChangePassword.aspx */
    #ChangePasswordForm,
    /* - WorkdayCalendar.aspx, QuotaCalendar.aspx, OvertimeAvailabilityCalendar.aspx */
    #Calendar,
    /* - WorkdayCalendar.aspx, QuotaCalendar.aspx */
    #WorkCalendar_Calendar_RadCalendar_wrapper,
    /* - OvertimeAvailabilityCalendar.aspx */
    #EventCalendar_Calendar_RadCalendar_wrapper {
      width: 100%;
      height: 100%;
    }
    /* - WorkdayCalendar.aspx, QuotaCalendar.aspx */
    #WorkCalendar_Calendar_RadCalendar,
    /* - OvertimeAvailabilityCalendar.aspx */
    #EventCalendar_Calendar_RadCalendar {
      width: calc(100% - 1px);
      height: 100%;
    }
    /* - WorkdayCalendar.aspx, QuotaCalendar.aspx */
    #WorkCalendar_Calendar_RadCalendar_Top,
    /* - OvertimeAvailabilityCalendar.aspx */
    #EventCalendar_Calendar_RadCalendar_Top {
      width: 100%;
      height: 100%;
    }
    /* - WorkdayCalendar.aspx, QuotaCalendar.aspx, OvertimeAvailabilityCalendar.aspx */
    .radCalDefault_GIRO, 
    .radCalHover_GIRO, 
    .radCalSelect_GIRO, 
    .radCalWeekendDefault_GIRO, 
    .otherMonth_GIRO {
      height: 0%;
    }
    .radCalDefault_GIRO #Day, 
    .radCalHover_GIRO #Day, 
    .radCalSelect_GIRO #Day, 
    .radCalWeekendDefault_GIRO #Day, 
    .otherMonth_GIRO #Day {
      background-repeat: repeat !important;
    }
    /* OvertimeAvailabilityCalendar.aspx */
    #EventCalendar_Calendar_RadCalendar_Top > tbody > tr {
      background-repeat: repeat !important;
    }
    .CalendarDayContent {
      position: absolute;
      top: 12px;
    }
    /* - WorkdayDetail.aspx */
    #WorkdayDetailView,
    /* - AbsencesDetail.aspx */
    #AbsencesDetailView,
    /* - PrintAbsences.aspx */
    #PrintAbsencesView,
    /* - OvertimeAvailabilityPeriod.aspx */
    #OvertimeAvailabilityPeriodView,
    /* - OvertimeAvailabilityTransactionDetail.aspx */
    #OvertimeAvailabilityTransactionDetailView,
    /* - OvertimeAvailabilityDetail.aspx */
    #OvertimeAvailabilityDetailView,
    /* - EmployeeProfile.aspx */
    #EmployeeProfileForm > div:nth-of-type(3),
    /* - ChangePassword.aspx */
    #ChangePasswordView {
      width: 96%;
      height: 96%;
      padding-top: 2%;
      padding-left: 2%;
    }
    /* WorkdayDetail.aspx */
    .CustomizableDetail {
      max-width: 800px;
    }
    /* - WorkdayDetail.aspx, AbsencesDetail.aspx, PrintAbsences.aspx, OvertimeAvailabilityPeriod.aspx, OvertimeAvailabilityTransactionDetail.aspx, OvertimeAvailabilityDetail.aspx, EmployeeProfile.aspx, ChangePassword.aspx */
    #HeaderTitle {
      margin: 0;
    }
  }
}
