css:
@-moz-document url("http://www.hktvmall.com/#fullscreen"),url("http://www.hktvmall.com/#tv") {
  * {visibility:hidden !important;}
  body {overflow: hidden !important;}
  #ott-player, #ott-player *,#ott-inner-wrapper, .ott-inner-wrapper * {visibility:visible !important;}
  #ott-player{width: 100% !important; height:100% !important;position:fixed !important;top: 0px !important;left: 0px !important;z-index:1 !important;z-index: 99999 !important}
  .ott-inner-wrapper {position:fixed !important;right:0 !important;top: 0 !important;z-index: 100000 !important;opacity: 0.5;}
  .ott-inner-wrapper #ott-playlist {display:none !important;}
  .ott-inner-wrapper:hover {opacity: 1;}
  .ott-inner-wrapper:hover #ott-playlist,#ottp-toggle-info {display: block !important;}
  #ottp-toggle-large {display: none !important;}
}
@-moz-document url-prefix("http://hktvlivechat."),domain("hktvchat.com") {
  #install_stylish.hktv0414 {display: none !important;}
}
