css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("inoreader.com") {
  .article_full_contents {
	  margin: 0 auto;
    margin-top: 1em;
    margin-bottom: 1em;
    padding: 1em;
		max-width: 1000px;
    box-shadow: 0px 0px 10px 4px #aaa;
    background-color: #F1F7FF;
	}
  
  .article_content, .article_footer{
    box-shadow: inset 0px 0px 5px 2px #aaa;
    padding: 2em 1em 2em 1em;
    margin:auto;
    max-width: 950px;
    background-color: #FFF;
  }
  .article_footer{
    padding: 0.3em 1em 0 1em;
    margin-top: 0.3em;
  }

  .article_content img, .article_content iframe, .article_content div{
    display:block;
    margin:auto;
  }
  
}
