@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("groupthink.jezebel.com"), domain("kinja.com"), domain("jezebel.com"), domain("gawker.com"), domain("io9.com") {
.js_replycol.js_ad300-companion > .js_branch-list.branch-list.all-threads.no-reply-counts >.branch.js_branch{
        width:45% !important;
    margin: 15px !important;
float:left!important;

vertical-align:top!important;


  
    }
  
    
    .section-header .txt{
        max-width: 50% !important;
    }


 .reply-border{
    

        border:none !important;
       
}
    
  .timeline-replies ,.branch .reply-footer-bar
 {
        border-top:1px solid #dcdcdc !important
    }
    .branch > .reply:hover{
border-left: 5px solid transparent!important
    }
   /* 
    
.strapline.proxima{
     padding-top:110px!important;
       
       */
    
    
    .featured-image.mbl > a > img {

display:none
}

        
    .logo{

         min-height:134px!important;
        width:470px!important; 

        
    }



    .has-media{
        
 
       width:100%!important;
       float:left!important  
    }
.media-300{
 min-width:100%!important   
}

.branch-wrapper,.post-list.hfeed{
        padding-top:90px!important
}
}

