@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("keep.google.com") {
  
  /* Black background */
  body { background-color: black; }
  
  /* Grey top-bar */
  #og-nwrapper > div > div, /* eliminates the need for all the other selections below */
  .gb_Ud .gb_3c,
  .gb_T  .gb_gc,
  .gb_Uc > .gb_R,
  .gb_Uc .gb_Od,
  .gb_Ld .gb_Tc,
  .gb_Bd .gb_Kc,
  .gb_3c .gb_bc,
  .gb_Zc .gb_4b,
  .gb_Xc .gb_3b,
  .gb_Xc .gb_6b,
  .gb_Xc .gb_5b,
  .gb_yd .gb_Hc {
    background: none repeat scroll 0% 0% #262626;
  }

  /* Light grey name text at top */
  #gb#gb a.gb_P, #gb#gb span.gb_P,
  #gb#gb a.gb_8a, #gb#gb span.gb_8a,
  #gb#gb a.gb_K, #gb#gb span.gb_K{
    color: lightgrey;
  }

  /* Font formatting */
  .IZ65Hb-YPqjbf {
    box-sizing: border-box;
    color: rgba(0, 0, 0, 0.8);
    font-family: "Roboto Condensed","Arial",sans;
    outline: medium none;
    white-space: pre-wrap;
    word-wrap: break-word;
  }
  
  /* Rounded edges */
  .IZ65Hb-QQhtn .IZ65Hb-TBnied .IZ65Hb-yePe5c,
  .XKSfm-L9AdLc .IZ65Hb-n0tgWb.oT9UPb,
  .ma6Yeb-r8s4j-gkA7Yd .IZ65Hb-TBnied,
  .IZ65Hb-TBnied, .TUdKmf-xhiy4 > .IZ65Hb-INgbqf  { border-radius: 10px; }
  
  /* Used to separate the new note tab and buttons, doesn't work, no longer needed
  .TUdKmf-xhiy4 > .IZ65Hb-n0tgWb { margin: 6px 6px 6px -10px; }
  */
  
  /* Dims background on selected Note */
  .XKSfm-L9AdLc-AHe6Kc { background-color: black; }
}
