css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.adpedge.com") {
   .ds-truncate-vin {
     font-size: 0;
     line-height: 0;
   }
   .ds-truncate-vin .ng-binding {
     font-size: 0;
     line-height: 0;
   }
  
  .ds-truncate-vin .ng-binding:after {
    content: attr(title);
    font-size: 14px;
    line-height: 1;
  }
  
}
