css:
@-moz-document domain("xqwh.org"),domain("162.218.49.230") {

/* 
Author: Wayzlin
Version: 1.1
Update Date: 2017/07/07
*/


/*更改字体*/
* {
    font-family:微软雅黑!important;
}

/*字体颜色*/
font[color="99CC00"] {
    color: #FFF !important;
    font-size: 16px;
    text-shadow: 0px 0px 2px #E9E6E8;
}
#pagecontent > table.tablebg.tabletopic > tbody > tr:nth-child(n) > td.row2.topictitle > span {
    color: #FFF !important;
}
span[style="color: #ffe7f7"],
span[style="color: #FFE7F7"],
span[style="color: #FFBFBF"] {
    color:rgba(255,255,255,0)!important;
}
span[style="color: #999933;"],
span[style="color: #999933"],
#smiley-page span,
font[id="message-note"][color="#999933"][size="-1"] {
    color: #00a36b !important;
}
[id^=p] > div > p {
    border-bottom-style:solid;
    border-width: 1px;
    border-color: #E9E6E8 !important;
}
#prefixsearchnew a:link,
#prefixsearchnew a:visited {
    color: #069 !important;
}
a:link,
a:visited,
span.switchsite {
    color: #1B8837 !important;
}
a:hover,
th a:hover,
a.topictitle:hover {
    color: #A65146 !important;
}

/*背景颜色*/
body {
    background: linear-gradient(0deg, #A4C0B4 0%, #B2D5C1 50%,#B2D4B9 100%);
}
.tablebg,
.postbody,
#menubar,
.tablebg tr,
.row1,.row2,.row3,
.cat,
td [class="spacer"][colspan="2"],
td.spacer {
    background:rgba(255,255,255,0)!important;
}
h2,
th {
    background: #D5EAD9 !important;
}
h2 {
    padding: 5px;
}
td[class="cat"][colspan="6"],
#menubar > table > tbody > tr > td:nth-child(1) > a > img {
    display:none !important;
}
th,
.tablebg,
.row1,.row2,.row3,
#pagecontent > table.tablebg.tabletopic td {
    border-collapse: collapse;
    border: solid #fff 1.5px;
}

}
