css:
@-moz-document domain("snopes.com") {
/* Gets rid of a crapton of useless things */
#q1-unit,
.siteHeader td:last-child,
.adColumn,
.article_text>center:nth-last-of-type(4),
.article_text>center:nth-last-of-type(3),
.article_text>center:nth-last-of-type(2),
.article_text>center:last-of-type,
noindex,
img[src$="content-divider.gif"],
br,
.siteFooterGradient,
img[src$="search-hdr.gif"],
a[href^="mailto:"],
#twitter-widget-0,
.utilityLinks,
.navSpacer,
.navHeader,
.navColumn>p,
.siteBottom,
iframe,
.searchBox td:last-child
{
  display: none;
}

.siteWrapper
{
  max-width: 100%;
}

/* Sets header to a not-ridiculous height */
.siteHeader,
.logo img
{
  height: 100px;
}

/* Sets width of content to fill all space available. */
.threeColumn
{
  width: 100%;
}

/* Pushes searchbox to the right */
.searchBox
{
  margin-right: 0;
  position: relative;
  top: -77px;
  right: -20px;
}

.contentColumn
{
  position: relative;
}

body
{
  background: #c7f2b1;
}

.siteWrapper
{
  background: white;
}

.siteContent,
.siteContent .contentArea
{
  background: aliceblue;
}

}
