css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.wuxiaworld.com/") {
  body {
    background: none !important;
    background-color: #393e49 !important;
    
  }
  
  .sp-body {
    background: none !important;
    background-color: #393e49 !important;
  }
  
  a {
    color: #C9C4C4;
  }
  
  a:hover {
    color: #bdc4ee; 
  }
  
  article {
    background-color: #292c33 !important;
    color: #afafaf;
    font-size: 1rem !important;
    width: 100% !important;
  }
  
  object {
    display: none;
  }
  
  #button {
    background: rgb(107, 113, 138) !important;
  }
  
  #comments {
     background-color: #292c33 !important;
    color: #afafaf;
    width: 100%;
  }
  
  #primary {
    width: 70%
  }
    
  
  #secondary {
    width: 28%;
    /*display:none; */
  }
  
  #site-navigation {
    background: none !important;
    background-color: #292c33 !important;
  }
  .widget {
    background-color: #292c33 !important;
    color: #afafaf;
    width: 100%;
  }
  
  .sp-wrap {
    background: none;
    background-color: #C9C4C4;
    border-color: #C9C4C4;
  }
}
