css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.idws.id") {


html{
background:url('http://i1359.photobucket.com/albums/q783/bonnybig/Houkago_zpsgyou6ftf.jpg') fixed center !important;
color:#eee !important;
text-shadow:rgba(0, 0, 0, 0.0) 1px 1px 0px !important;
}
#content .pageContent, .sectionMain
{background: none !important;}
#headerMover #headerProxy
{background: none repeat scroll 0% 0% rgba(242, 134, 233, 0.6)}
#moderatorBar,.footer .pageContent,.navTabs
{background: #864D92}
.navTabs .navTab.selected .tabLinks
{background: none !important;}
.primaryContent
{background-color: #B6AAA7; border-radius: 20px}
readcrumb
{background: #ED8360}
div#logo, div#cboxdiv
{display: none}
.bbCodeQuote .attribution
{background-color: #E1A557}
.bbCodeQuote .quoteContainer
{background-color: #EDC7A7}
.nodeList .categoryStrip
{background: #FFABF2; border-radius: 20px}
.sectionMain
{border-radius: 50px; border: 5px solid #EDC7A7}
.nodeList .node.level_1
{border-bottom: 200px}
.message .messageInfo
{border: 5px solid #FFABF2}
.discussionList .sectionHeaders
{background: #F6BDEB}
.node .forumNodeInfo.unread .nodeIcon, .node .categoryForumNodeInfo.unread .nodeIcon
{background:url(http://i1359.photobucket.com/albums/q783/bonnybig/ana_zpsfotn5dw6.jpg) no-repeat !important}
}
