/* --- Ad-Remover --- */

	.yamplus-unit-container {display:none!important;}

	.stretchy_kids {display:none!important;}

.sponsored-day-media-section {
display:none!important;}

.remnant_ad {
display:none!important;
}

.li.recommended-unit-container {
display:none!important;
}
