css:
@-moz-document domain(slack.com) {
/* slack */
.light_theme .message_content, #message-input {
  font-family: IRAN-Sans, Tahoma;
  font-size: 13px;
  line-height: 1.8;
  direction: rtl;
  width: 50%;
  text-align: left;
}
  #message-input { width: 100%; }

.comment_body, .comment .special_formatting_quote .content, .comment,
.comment_input.small_bottom_margin, .file_header .title {
  font-family: IRAN-Sans, Tahoma;
  font-size: 13px;
  line-height: 1.8;
  direction: rtl;
  text-align: left;
}
   
a.file_preview_link.file_name {
  direction: rtl;
  display: inline-block;
  font-family: IRAN-Sans, 'IRAN Sans', Tahoma;
  font-size: 12px;
}
    
span.emoji-sizer, inline_attachment .inline_attachment_wrapper, .internal_member_link, .inline_attachment {
  direction: ltr;
}
  .internal_member_link { display: inline-block; direction: rtl; }

.attachment_contents {
  direction: rtl;
}
      
.light_theme .message {
  margin: 1.3rem 0 !important;
}
.light_theme .message:not(.show_user) {
  margin: -1.3rem 0 1.3rem !important;
}
}
