css:
@-moz-document domain(tabun.everypony.ru) {
	.strength, .rating, .vote-profile, .vote {
  		display: none;
	}
	td.cell-rating, td.cell-skill {
	    color: transparent !important;
	}
	td.cell-rating:after, td.cell-skill:after {
	    content: "="; position: relative; top: -8px; display: block; color: #888 !important;
	}
}
