css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ameblo.jp") {
  
  /* AD */
  .adMenu,.category-alliance-wrapper,.subAdBannerArea,#footer_ad,.subAdBannerHeader,.afc-frame,div[id^="google_ads"] {display:none !important;}
  div[style="border: 1px solid #ccc; height:130px"] {display:none !important;}
  
 /* ヘッダー */
body,html.fixed body {padding-top: 0 !important;}
  #ambHeader {display:none !important;}
  .skinFrame {padding-top: 0 !important;}
  
  /* ナビ */
  ul.footerNav {display:none !important;}
  
  /* このブログの読者になる */
  a[href^="http://blog.ameba.jp/reader.do?bnm="] {display:none !important;}
  /* プロフィール　ソーシャル要素 */
  a.amemberBtn,a.presentBtn,a.messageBtn,a[href^="http://r.ca-mpr.jp"] {display:none !important;}
  .profileUserNicknameArea .profileUserPages:last-child {display:none !important;}

  /* このブログの読者,ランキング */
.readerMenu,.rankingMenu {display:none !important;}
  
  /* アメーバID登録（無料）*/
  .registBtnArea {display:none !important;}
  
  /* フッター */
  #js-bigfooter,#ambFooter{display:none !important;}
  
  /* ソーシャル */
  .skinArticleFooter {display:none !important;}
  
  /*※著作権についてのご注意*/
  a[href="http://helps.ameba.jp/trouble/copyright.html"] {display:none !important;}

}
