css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("jvforum.fr") {
body {
    background: none repeat scroll 0% 0% #E5E5E5;
    background-color: #3366FF;
    background-repeat: no-repeat;
    background-image: linear-gradient(#00CCFF, #3366FF);
    padding-bottom: 20px;
}

.sheet {

    border-color: #000BEA;
    box-shadow: 0px 0px 10px rgba(0, 61, 234, 0.5);
}
    .forum-title a {
    color: #59AAC6;
    }
.form-post {
    margin-top: 10px;
    border: 1px solid #59AAC6;
    border-radius: 1px 8px 1px 5px;
}
.form-post .titre-bloc {
    background: none repeat scroll 0% 0% #59AAC6;
}
    
    .pages .active {
    background: none repeat scroll 0% 0% #006FFF;
    color: #FFF;
    font-weight: bold;
}

.liste-topics .title {
    padding-top: 5px;
}
}
