css:
@-moz-document domain("benzclub.ru") {
body > div > div.page > div > table.tborder > tbody > tr > td.alt2, td[valign="top"][width="245"],
div.header, br, table.ncode_imageresizer_warning, .footer .imageText {display: none!important;}

img.attach, 
div[id^="post_message_"] > img, 
img[id^="ncode_imageresizer_container_"], 
img[onload^="NcodeImageResizer.createOn(this);"] 
{max-width: 100%!important; height: auto!important;}

/*Исправление переносов в постах*/
tr > td[id^="td_post_"] > div[id^="post_message_"], 
div[id^="post_message_"] > div > table > tbody > tr > td > div[style="font-style:italic"] 
{white-space: pre-wrap!important;}

div[id^="post_message_"] > div[style^="margin:20px; margin-top:5px;"], 
table[id="vB_Editor_QE_1_editor"], .bbf_slide,
tr > td[id^="td_post_"] > div[id^="post_message_"] > div,
div[id^="post_message_"] > div > table > tbody > tr > td.alt2 
{white-space: normal!important;}
}
