css:
@-moz-document domain("allboxing.ru") {
/* Рейтинг p4p */

div.p4p-li {display:inline-table!important; margin-bottom: 0px!important; height: 0px!important; padding: 21px 5px 0px!important;}

#p4p > div > div > div > div > div > div > a:nth-child(3) > h3 {font-family: helvetica!important; font-size:13px!important; font-weight: 500!important;}

/* Шрифты */

.breadcrumbs a, .breadcrumbs, span.grey-text, span.date-display-single {font-family: helvetica!important}

/* Правка положения блока */

.block-news-mma {width:auto!important}
}

@-moz-document domain("allboxing.ru") {
/* Расписание и результаты */

#table > div > div > div > ul > li, 
#results > div > div > div > ul > li 
{display:inline-table!important; width:315px; padding:13px 0 13px!important;} 

#table > div > div > div > ul > li > div > h4, 
#results > div > div > div > ul > li > div > h4 
{font-size:13px!important; font-weight: 500!important; max-height: 2.4em!important; overflow: hidden!important; font-family: helvetica!important;}
}
