css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://seiryu"),url-prefix("http://www1.plala.or.jp/seiryu") {
body{
  background: #222222;
  color: white;
  font-family : "Meiryo";
    line-height:1.6em !important;
  letter-spacing:0.8pt !important;
  }
  
  font[color="#000000"]{color: white;}
  
a:link {
color:#23466e;
}
a:hover {
color:#7e8639;
}
a:visited {
color:#934e61;
}
  
  table,tr,th,td{background:#151515;
}
 
}

/* TOPページ　*/
@-moz-document url("http://www1.plala.or.jp/seiryu/contents.htm"){
  a[href^="http://hpcgi1.nifty.com/gc/ring/"],a[href^="http://homepage1.nifty.com/gc/ring/"]{display: none;}
}

/* 個別ページ */
@-moz-document url-prefix("http://seiryu"){
}
  
  /* 道場主雑記(ブログ) */
