@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("goodfon.ru"), domain("goodfon.su"){
   
   html, body{background: rgba(0,0,0, .05)url(/*[[URL]]*/)!important; 
  background-color: #111 !important;	background-attachment: fixed !important;	background-clip: border-box !important;	background-origin: padding-box !important;
	background-position: center center !important;	background-repeat: no-repeat !important;	background-size: cover !important;  font-family:Constantia!important} 
  
   body>div:nth-child(1)>div:nth-child(1)>div:nth-child(1)>a:nth-child(1)>img:nth-child(1){opacity:.0!important}
   body > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) {background: url(/*[[logo]]*/) no-repeat scroll 0% 0% transparent !important;background-size: contain !important}
   body > div:nth-child(1) > div:nth-child(9){background: url(https://vhelbling.files.wordpress.com/2011/04/dropsplash_black.png) no-repeat scroll 0% 0% transparent !important;background-size: contain !important;background-position:center center !important}
   div.grid_12:nth-child(7) > img:nth-child(1) , div.grid_12:nth-child(7) > img:nth-child(2){opacity:.1!important}
  .pageinfo{opacity:.6!important}
  
  #sl {background: rgba(0, 0, 0,  0.8)!important; box-shadow: 0px 0px 4px rgba(0, 200, 255,  .9 )inset !important;border:none!important;color:#33ccee!important}
  .tabl_td, a.menu:hover, .silver_border, table.silver_border>tbody>tr>td>input, .select, .blockhead, div.grid_8>div>div>div>div, #back-top, .loginBlock, #login, #email, #password, #passwordAgain, #captcha-form, #remindEmail, #loginLogin, #loginPass, .paginator span strong, .user_block, .user_menu a:hover, .searchInput, ul.multiple {background: rgba(0, 0, 0,  0.8)!important; box-shadow: 0px 0px 2px rgba(0, 200, 255,  .9 ) !important}
  
 .featuree, .container_12, body>table>tbody>tr>td, #\30>textarea, #back-top:hover {background: rgba(0, 0, 0,  0.9)!important; box-shadow: 0px 0px 6px rgba(0, 200, 255,  .9 )inset !important}
    
  .paginator .scroll_trough {background-color: #000 !important; background: linear-gradient(to bottom, #33ccee 0%, #3399ff 100%) !important}
  #footer{background-color: #000 !important; background: linear-gradient(to bottom, #000 0%, #333 100%) !important}
    
  .tabl_td,.menu, a.menu, a.menu:visited, #titlebar, div.grid_8:nth-child(9)>div:nth-child(3)>div:nth-child(1), div.grid_8:nth-child(9) > div:nth-child(3) > div:nth-child(2), div.grid_8:nth-child(9) > div:nth-child(3) > div:nth-child(2) > div:nth-child(1), .rdnBlock, hr, div.grid_12>table>tbody>tr>td, div.grid_12>table>tbody>tr>td>b, ul.multiple li label {background:transparent!important}
  .ps, .menus, .silver_border, .silver_border, table.silver_border>tbody>tr>td>input, .select, #\30>textarea, .loginBlock, #login, #email, #password, #passwordAgain, #captcha-form, #remindEmail, #loginLogin, #loginPass, div.grid_12>table>tbody>tr>td, div.user_block>table>tbody>tr>td, .searchInput, ul.multiple {border:none!important}
     
  div.container>div>div, a:hover, body, a.menu:hover, .loginBlock input, ul.multiple li label, .grey {color: #33EEFF !important; text-decoration: none !important;}
  small, a:link, a:visited, .menus, ul.multiple li label:hover {color: #3399ff !important; text-decoration: none !important;}
  
 .wal_banner, #yandex_ad_R-61214-1,div.container:nth-child(2)>div:nth-child(9)>div:nth-child(1), #_sbn1_div_,body > div:nth-child(1) > div:nth-child(32),     div.container:nth-child(2)>div:nth-child(32)>div:nth-child(1)>div:nth-child(1), body > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > div:nth-child(7), div.grid_4:nth-child(10) > div:nth-child(1), body > div:nth-child(1) > div:nth-child(9) > div:nth-child(1),
   div.grid_12:nth-child(7) > noindex:nth-child(1) > div:nth-child(1)
   {display:none!important}}
