css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("https://login.bme.hu/idp/Authn/UserPassword") {
body{
  
 background-color: black;
 font-family: Verdana;
  background-image: url("http://shinnok.com/rants/wp-content/uploads/2011/06/minimalist-wallpaper-black.jpg");
  background-position:center; 
  background-repeat:no-repeat;
  background-attachment: fixed;
  background-size: cover;
}

  
  .clear{display:none;}
  
  

#user-box a {border: 1px solid black;
    color: white;
    padding: 2px 15px;
    text-decoration: none;
    background-color: #e54d02;
}
  h3 a{color: #313131!important;
  }
  
  #content{
    background-color: #1d1e20;
    color: white;
  }
  
  #content a{
    color: red;
    font-style: italic;
    text-decoration: none;
  }
  
  #content a:hover{
    color: #f77105;
  }
  

  
  input[type="submit"]{
    width: 11em;
    margin: 0 0 0 34%;
    background-color: orange;
    border: 2px solid black;
    color: white;
    
    text-transform:uppercase;
    font-weight: bold;
}
input[type="submit"]:hover{
  background-color: green;
}

#footer {
    display:none;
}
 

}
