css:
@-moz-document url("https://sso.acu.edu/login?service=https%3A%2F%2Fmy.acu.edu%2Fc%2Fportal%2Flogin%3Fembed&windowTarget=top") {
input[type="submit"]
{
  display: inline-block;
  height: 31px;
  padding: 0 24px;
  color: #fff;
  text-align: center;
  font-size: 11px;
  font-weight: 600;
  line-height: 31px;
  letter-spacing: .1rem;
  text-transform: uppercase;
  text-decoration: none;
  white-space: nowrap;
  background-color: #4d3766;
  border-radius: 4px;
  border: 1px solid #bbb;
  cursor: pointer;
  box-sizing: border-box;
  font-family: sans-serif;
  margin-right:10px;
  margin-bottom:3px;
  margin-top:5px;
}

input[type="email"],
input[type="number"],
input[type="search"],
input[type="text"],
input[type="tel"],
input[type="url"],
input[type="password"],
textarea,
select {
  height: 24px;
  padding: 4px 5px; /* The 6px vertically centers text on FF, ignored by Webkit */
  background-color: #fff;
  border: 1px solid #D1D1D1;
  border-radius: 4px;
  box-shadow: none;
  box-sizing: border-box;
  margin-bottom: 8px;
}

.link{
    display: none;
}
}
